	import { getDatabase, ref, set, onValue } from 'firebase/database';

	const firebaseConfig = {
		apiKey: 'AIzaSyBy25mTaZchSAfi7A-JhthBAF2WBIVcSLA',
		authDomain: 'landing-test-4c334.firebaseapp.com',
		projectId: 'landing-test-4c334',
		storageBucket: 'landing-test-4c334.appspot.com',
		messagingSenderId: '180667958636',
		appId: '1:180667958636:web:6e39123ae9ee2732c61f49',
		databaseURL: 'https://landing-test-4c334-default-rtdb.firebaseio.com/'
	};

	//
	let x = 0;
	let y = 0;
	let color = 'blue';
	let name = 'Sarah';
	//
	let otherUser = {
		x: 0,
		y: 0
	};

	let otherUsers;

	function getCursorPosition(event) {
		const { clientX, clientY } = event;
		x = clientX;
		y = clientY;
	}

	const colors = [
		'#d90429',
		'#ffa600',
		'#3182ce',
		'#718096',
		'#d63384',
		'#9a0e3f',
		'#ffc107',
		'#9b4dca',
		'#5a67d8',
		'#ed64a6',
		'#550077'
	];

	const names = ['Sarra', 'John', 'Rob', 'Erman', 'Mariana', 'Fons', 'Sara', 'Sarah'];

	// function that randomly returns a color from the colors array
	function getRandomVariables() {
		color = colors[Math.floor(Math.random() * colors.length)];
		name = names[Math.floor(Math.random() * names.length)];
	}
	let app;
	let database;
	onMount(() => {
		getRandomVariables();
		app = initializeApp(firebaseConfig);
		database = getDatabase(app);
		if (localStorage.getItem('userid') === null) {
			generateUserId();
		}

		// getFromDB();
		getNumberOfUsers();
	});
	function generateUserId() {
		const userId = Math.floor(Math.random() * 1000000);
		localStorage.setItem('userid', JSON.stringify(userId));
	}

	function writeUserData() {
		const db = getDatabase();
		set(ref(db, 'users/' + localStorage.getItem('userid')), {
			x: x,
			y: y
		});
	}
	async function getFromDB() {
		const db = getDatabase();
		const starCountRef = ref(db, 'users/784767');
		onValue(starCountRef, (snapshot) => {
			const data = snapshot.val();
			console.log('data: ', data);
			otherUser = data;
		});
	}

	async function getNumberOfUsers() {
		const db = getDatabase();
		const starCountRef = ref(db, 'users');
		onValue(starCountRef, (snapshot) => {
			const data = snapshot.val();
			otherUsers = data;
		});
	}
</script>

